$( "#btn" ).click(function() {
  //alert( "Handler for .click() called." );


 
  var username =$("#username").val(); // get username value
  var detailsname = $("#detailsname"); // get value field for set the value 
  $(detailsname).text("Username :"+""+username); // show and set value 
  $("#username").val("");

 
   var age =$("#age").val(); // get age value
   var numage = parseInt(age); // convert to int
   var detailsage = $("#detailsage"); //get value field for set the data
   $(detailsage).text("User age :"+""+numage); // show and set value 
   $("#age").val("");// clear



   var weight =$("#weight").val();
   var height =$("#height").val();
   var result =$("#resultbmi");

   var bmifinal = (weight/((height*height)/10000)).toFixed(2);
   if( bmifinal < 18.6){
   // alert("sdkbvshv");
   
    $(result).text("UNDER WEIGHT :" +"  "+ bmifinal);
    
  }else if (bmifinal >= 18.6 &&  bmifinal < 24.9){
   // alert('hdakn ad bbbbbbbbbbbbbbbbbbbbbbbbbbbb');
    
     $(result).text("NORMAL WEIGHT :" +"  "+ bmifinal);

      
  }else{
        
        $(result).text("OVER WEIGHT  :" +"  "+ bmifinal);
            
  }
   
  $("#weight").val("");
  $("#height").val("");
   

});